function App()
{
  //This func is used for dispalying the status of a day
  const date=new Date();
  const hours=date.getHours();
  let timeDay;
  const styles={
   fontSize: 45
  }
  if(hours< 12)
  {
    timeDay="Its Morning";
  }
  else if(hours>12 && hours<17)
  {
    timeDay="its afternoon";
  }
  else{
    timeDay="its evening";
  }
  return(
    <h1 style={styles}>{timeDay}</h1>
  )
}

export default App
