import React from "react"

function Exp()
{
  //This func is used for dispalying the status of a day
  const date=new Date();
  const hours=date.getHours();
  let timeDay;
  //Adding inline css
  const styles={
   fontSize: 45, color:'purple'
  }
  if(hours< 12)
  {
    timeDay="Its Morning";
  }
  else if(hours>12 && hours<17)
  {
    timeDay="Its afternoon";
  }
  else{
    timeDay="Its evening";
  }
  return(
    <h1 style={styles}>{timeDay + " " + date}</h1>
  )
}


export default Exp;
