function App()
{
  const date=new Date();
  const hours=date.getHours();
  let timeDay;
  let ho=hours%12;
  if(ho< 12)
  {
    timeDay="Good Morning";
  }
  else if(ho>12 && ho<15)
  {
    timeDay="Good afternoon";
  }
  else{
    timeDay="Good evening";
  }
  return(
    <h1>{timeDay+" "+hours+" "+ho}</h1>
  )
}

export default App
